const deviceList = $input.all();
const borrowRequest = $("借用申请触发").all();

const result = borrowRequest.map((request) => {
  const deviceNumber = request?.json["设备编号"];
  const deviceName = request?.json["设备名称"];

  const isExist = deviceList.some((device) => {
    return (
      device?.json["设备编号"] === deviceNumber &&
      device?.json["设备名称"] === deviceName
    );
  });

  return { json: { isExist }, pairedItem: deviceList.map((_, index) => index) };
});

return result;

