const deviceList = $input.all().map((item) => item.json);
const returnRequests = $("归还申请触发")
  .all()
  .map((item) => item.json);

const result = returnRequests.map((request) => {
  const matchedDevice = deviceList.find(
    (device) =>
      device["设备编号"] === request["设备编号"] &&
      device["借用人邮箱"] === request["邮箱地址"] &&
      device["当前使用者"] === request["CDSID"],
  );
  return { json: { exists: !!matchedDevice } };
});

return result;
